package cimat.tesis.sna.patterns.utils;

import java.util.Comparator;

public class DegreeComparator  implements Comparator<MyVertexDegree>{

	@Override
	public int compare(MyVertexDegree vd1,
			MyVertexDegree vd2) {
		// TODO Auto-generated method stub
		if(vd1.getDegree() == vd2.getDegree()) return 0;
		if(vd1.getDegree() > vd2.getDegree()) return -1;
		else return 1;
	}
	
}